import { GET_LOGS, DELETE_LOG, ADD_LOG, SET_CURRENT, CLEAR_CURRENT, SEARCH_LOGS, UPDATE_LOG, SET_LOADING, LOGS_ERROR, GET_DEVELOPER, DEVELOPER_ERROR, ADD_DEVELOPER } from './types';

// Action to get logs
export const getDevelopers = () => async dispatch =>  {
    try {
        // Set Loading To True
        setLoading();
        // Fetch from api
        const res = await fetch('/developers');
        const data = await res.json();
        // Dispatch
        dispatch({
        type: GET_DEVELOPER,
        payload: data
