import React, { useState } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { addLog } from '../../actions/logActions';
import M from 'materialize-css/dist/js/materialize.min.js';

const AddLogModal = ({ addLog }) => {
    const [message, setMessage] = useState('');
    const [attention, setAttention] = useState('');
    const [developer, setDeveloper] = useState('');

    const onSubmit = () => {
        if(message === '' || developer === '') {
            M.toast({ html: 'Please provide values for message and developer' });
        } else {
            const newLog = {
                message,
                attention,
                date,
                developer
            }
            setMessage('');
            setAttention(false);
            setDeveloper('');
        }
    }

    return (
        <div id='add-log-modal' className="modal">
            <div className="modal-content">
                <h4>Enter Developer Log</h4>
                <div className="row">
                    <div className="input-field">
                        <input type="text"
                            name="message"
                            value={message}
                            onChange={e => setMessage(e.target.value)} />
                        <label htmlFor="message" className="active">Developer Log</label>
                    </div>
                </div>
                <div className="row">
                    <div className="input-field">
                        <select name="developer"
                            value={developer}
                            className="browser-default"
                            onChange={e => setDeveloper(e.target.value)}
                        >
                            <option value="" disabled>Select Developer</option>
                            <option value="Mohammad Ali">Mohammad Ali</option>
                            <option value="Mohammad Hadi">Mohammad Hadi</option>
                        </select>
                    </div>
                </div>
                <div className="row">
                    <div className="input-field">
                        <p>
                            <label>
                                <input type="checkbox"
                                    className="filled-in"
                                    checked={attention}
                                    onChange={e => setAttention(!attention)}
                                />
                                <span>Needs Attention</span>
                            </label>
                        </p>
                    </div>
                </div>
            </div>
            <div className="modal-footer">
                {/* eslint-disable-next-line */}
                <a href="#" onClick={onSubmit}
                    className="modal-close waves-effect waves-blue btn-flat"
                >
                    Submit</a>
            </div>
        </div>
    )
}

AddLogModal.propTypes = {
    addLog: PropTypes.object.isRequired,
}

export default connect(null, { addLog })(AddLogModal);
