import { GET_LOGS, DELETE_LOG, ADD_LOG, SET_CURRENT, CLEAR_CURRENT, UPDATE_LOG, SET_LOADING, LOGS_ERROR } from './types';

// Action to get logs
export const getLogs = () => async dispatch =>  {
    try {
        // Set Loading To True
        setLoading();
        // Fetch from api
        const res = await fetch('/logs');
        const data = await res.json();
        // Dispatch
        dispatch({
        type: GET_LOGS,
        payload: data
        })
    } catch (err) {
        dispatch({
            type: LOGS_ERROR,
            payload: err.response.data
        })
    }
}

// Action to add a log
export const addLog = (log) => async dispatch =>  {
    try {
        // Set Loading To True
        setLoading();
        // Fetch from api
        const res = await fetch('/logs', {
            method: 'POST',
            body: JSON.stringify(log),
            headers: {
                'Content-Type': 'application/json'
            }
        });
        const data = await res.json();
        // Dispatch
        dispatch({
        type: ADD_LOG,
        payload: data
        })
    } catch (err) {
        dispatch({
            type: LOGS_ERROR,
            payload: err.response.data
        })
    }
}

// Action to delete a log
export const deleteLog = (id) => async dispatch =>  {
    try {
        // Set Loading To True
        setLoading();
        // Fetch from api
        await fetch(`/logs/${id}`, {
            method: 'DELETE'
        });
        // Dispatch
        dispatch({
        type: DELETE_LOG,
        payload: id
        })
    } catch (err) {
        dispatch({
            type: LOGS_ERROR,
            payload: err.response.data
        })
    }
};

// // Action to set the current log
export const setCurrent = log =>  {
    return {
        type: SET_CURRENT,
        payload: log
    }
}

// Set Loading To True
export const setLoading = () => {
    return {
        type: SET_LOADING
    }
}
