import { GET_DEVELOPER, ADD_DEVELOPER, DELETE_DEVELOPER, DEVELOPER_ERROR } from '../actions/types';

const initialState =  {
    developers: null,
    loading: false,
    error: null
}

export default (state = initialState, action) => {
    switch (action.type) {
        case GET_DEVELOPER:
            return {
                ...state,
                developer: action.payload,
                loading: false
            }
        case ADD_DEVELOPER:
            return {
                developer: [...state.developer, action.payload],
                loading: false 
            }
        case DELETE_DEVELOPER:
            return {
                ...state,
                developer: state.developers.filter(developer => developer.id !== action.payload),
                loading: false
            }
        case DEVELOPER_ERROR:
            return {
                ...state,
                error: action.payload
            }
        default:
            return state;
    }
}
