import React, { useState, Fragment } from 'react';
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';
import Navbar from './components/layout/Navbar';
import Users from './components/users/Users';
import Search from './components/users/Search';
import Alert from './components/layout/Alert';
import About from './components/pages/About';
import User from './components/users/User';
import axios from 'axios';
import './App.css';

class App extends Component {
  state = {
    users: [],
    user: {},
    repos: [],
    loading: false,
    alert: null
  }

  // This function is called from the Search component by passing props up
  searchUsers = async (text) => {
    this.setState( { loading: true } );
    const res = await axios.get(`https://api.github.com/search/users?q=${text}&client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`)
    this.setState( { users: res.data.items, loading: false } )
  }

  // This function is called from the User Item Component to display User Details from GitHub
  getUser = async (username) => {
    this.setState( { loading: true } );
    const res = await axios.get(`https://api.github.com/users/${username}?client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`)
    this.setState( { user: res.data, loading: false } )
  }

  // This function is called from the User component to display the latest repos from GitHub
  getUserRepos = async (username) => {
    this.setState( { loading: true } );
    const res = await axios.get(`https://api.github.com/users/${username}/repos?per_page=5&sort=created:asc&client_id=${process.env.REACT_APP_GITHUB_CLIENT_ID}&client_secret=${process.env.REACT_APP_GITHUB_CLIENT_SECRET}`)
    this.setState( { repos: res.data, loading: false } )
  }

  // This function is called from the Search component to clear users from state
  clearUsers = () => this.setState({ users: [], loading: false })

  // This function is called from the Search component to raise an alert for empty text field search
  setAlert = (msg, type) => {
    this.setState({ alert: { msg, type } })
    // Remove the Alert message after 3 second
    setTimeout(() => this.setState({ alert: null }), 3000)
  }

  render() {
    const { users, user, repos, loading } = this.state;

    return (
      <Router>
        <div className="App">
          <Navbar />
          <div className="container">
            <Alert alert={this.state.alert} />
            <Switch>
              {/* Route for the home page */}
              <Route exact path='/' render={ props => (
                <Fragment>
                  <Search searchUsers={this.searchUsers} 
                  clearUsers={this.clearUsers}
                  showClear={users.length > 0 ? true : false}
                  setAlert={this.setAlert}
                  />
                  <Users loading={loading} users={users} />
                </Fragment>
              )} />
              {/* Route for the about page */}
              <Route exact path='/about' component={About} />
              {/* Route for the User Details pages */}
              <Route exact path='/user/:login' render={ props => (
                <User {...props} getUser={this.getUser} getUserRepos={this.getUserRepos} user={user} repos={repos} loading={loading} />
              )} />
            </Switch>
          </div>
        </div>
      </Router>
    );
  }
}

export default App;
