import React, { Fragment, Component } from 'react';
import Spinner from '../layout/Spinner';
import PropTypes from 'prop-types';
import { Link } from 'react-router-dom';


class User extends Component {
    componentDidMount() {
        this.props.getUser(this.props.match.params.login);
    }

    static propTypes = {
        getUser: PropTypes.func.isRequired,
        user: PropTypes.func.isRequired,
        loading: PropTypes.bool.isRequired,
    }

    render() {
        const { name, company, avatar_url, location, bio, blog, login, html_url, followers, following, public_repos, public_gists, hireable } = this.props.user;

        const { loading } = this.props;

        if (loading) return <Spinner />

        return (
            <Fragment>
                <Link to='/' className="btn btn-light">Back to home</Link>
                Hireable: { hireable ? <i className="fas fa-check-success"></i> : <i className="fas fa-times-circle text-danger"></i> }
                <div className="card grid-2">
                    <div className="all-center">
                        <img src={avatar_url} alt="Profile Picture" className="round-img" style={{ width: '150px' }} />
                        <h2>{name}</h2>
                        {location && (<h5>Location: {location}</h5>) || ('The location of this user is not available')}
                    </div>
                    <div>
                        {bio && (
                            <Fragment>
                                <h3>Bio</h3>
                                <p>{bio}</p>
                            </Fragment>
                        )}
                        <a href={html_url} className='btn btn-dark my-1' target='_blank' >GitHub Profile</a>
                        <ul>
                            <li>
                                {login && (
                                    <Fragment>
                                        Username: {login}
                                    </Fragment>
                                )}
                            </li>
                            <li>
                                {company &&
                                <Fragment>
                                    Company: {company}
                                </Fragment>
                                }
                            </li>
                            <li>
                                {blog &&
                                <Fragment>
                                    Website: {blog}
                                </Fragment>
                                }
                            </li>
                        </ul>
                    </div>
                </div>
                <div className="card text-center">
                    <div className="badge badge-primary">Follolwers: {followers}</div>
                </div>
            </Fragment>
        )
    }
}

export default User
