import React from 'react';
import PropTypes from 'prop-types'

const Search = ({ searchUsers, showClear, clearUsers,setAlert }) => {

    state = {
        text: ''
    }

    // This method is used to dynamically update state whenever text input changes in the search form
    const onChange = e => {this.setState({ [e.target.name]: e.target.value })}

    // This method is used to submit the form
    const onSubmit = e => {
        e.preventDefault();
        if(this.state.text==='') {
            this.props.setAlert('Please enter a username', 'light')
        } else {
            this.props.searchUsers(this.state.text);
            this.setState({ text: '' });
        }
    }

        return (
            <div>
                <form onSubmit={this.onSubmit} className="form">
                    <input type="text" name="text" placeholder="Search Users"
                        value={this.state.text}
                        onChange={this.onChange}
                    />
                    <input type="submit" value="Search" className="btn btn-dark btn-block" />
                </form>
                {showClear && (
                    <button onClick={clearUsers} className="btn btn-light btn-block">Clear</button>
                )}
            </div>
        )
    }

Search.propTypes = {
    searchUsers: PropTypes.func.isRequired,
    clearUsers: PropTypes.func.isRequired,
    showClear: PropTypes.bool.isRequired,
    setAlert: PropTypes.func.isRequired,
}

export default Search
